$(function(){
    $.ajax({
        url:'test.xml',
        type:'GET',
        dataType:'xml',
        timeout:1000,
        error:function() {
            alert("ロード失敗");
        },
        success:function(xml){
            $(xml).find("sample").each(function() {
                $("#sample ul").append('<li><a href="' + $(this).find('url').text() +  '" target="_blank">' + $(this).find('title').text() + '</a></li>');
            });
        }
    });
});

